  ('13','Clear Comments','<strong>1.0</strong> Remove comments of documents when you empty MODx Trash Can.','0','0','0','/**\n * Clear Comments Plugin: Remove comments of documents when you empty MODx Trash Can.\n * Version: 1.0\n * Event: \"OnEmptyTrash\"\n * Requirement: Jot Snippet\n * Work on: Evolution\n *\n * Author: AHHP ~ Boplo.ir\n * Date: 21 August 2009\n*/\n\ndefined(\'IN_MANAGER_MODE\') or die();\nif($modx->Event->name == \"OnEmptyTrash\")\n{\n	$where = \'uparent IN(\' .join(\',\' , $ids). \')\';\n	$jot_content = $modx->getFullTableName(\'jot_content\');\n	$jot_fields = $modx->getFullTableName(\'jot_fields\');\n	$jot_subscriptions = $modx->getFullTableName(\'jot_subscriptions\');\n	\n	\n	// If `jot_fields` exists\n	if($modx->db->getRecordCount( $modx->db->query(\"SHOW TABLES LIKE \'$jot_fields\'\") ) == 1)\n	{\n		// `jot_fields` stores fields by comments ID so we need to get comments ID that are removing.\n		$commentsIds = array();\n		$select = $modx->db->select(\"id\", $jot_content, $where);\n		while($commentRow = $modx->db->getRow($select, \'num\'))\n			$commentsIds[] = $commentRow[0];\n		\n		if(count($commentsIds) > 0)\n			$modx->db->delete($jot_fields, \'id IN(\' .join(\",\",$commentsIds). \')\');\n		\n		unset($select, $commentsIds);\n	}\n	\n	$modx->db->delete($jot_content, $where);\n	$modx->db->delete($jot_subscriptions, $where);\n}','0','','1','','0','0'),
  ('14','JotAdminNotify','<strong>0.1</strong> На главной странице админки отображается количество неопубликованных коментариев снипета JotX','0','0','0','$output = \"\";\n$e = &$modx->Event;\nif($e->name == \'OnManagerWelcomePrerender\'){\n\n    $table = $modx->getFullTableName(\'jot_content\');\n    $sitecontent = $modx->getFullTableName(\'site_content\');\n    $rs = $modx->db->query(\"SELECT count(jc.uparent) as count, jc.uparent, sc.pagetitle FROM $table jc left join $sitecontent sc on sc.id = uparent where jc.published=0 group by jc.uparent\");\n    while($row=$modx->db->GetRow($rs)){\n        if ($row[\'count\']>0){\n            $id = $row[\'uparent\'];\n            $count = $row[\'count\'];\n            $url = $modx->makeUrl($id);\n            $output .= \"<li><a href=\'$url\' target=\'_blank\'>\".$row[\'pagetitle\'].\": $count</a></li>\";\n        }\n    }\n    \n    if (!empty($output)){\n        $output = \'<div class=\"sectionHeader\" style=\"color:red\">Имеются неопубликованные комментарии</div><div class=\"sectionBody\"><ul>\'.$output.\'</ul></div>\';\n    }\n    $e->output($output);\n}\n','0','','1',' ','0','1527665223'),
